(111, 'widget_custom_html', 'a:5:{i:3;a:6:{s:5:"title";s:0:"";s:7:"content";s:253:"<div class="cont grey ph30">\r\n<div class="wrapper">\r\n	<div class="twm-block">\r\n		<div class="text">Still curious who I am? Send me an email and ask me personally.</div>\r\n   	 	<a href="/contacts/" class="button">Ask here</a>\r\n	</div>\r\n</div>    \r\n</div>";s:10:"dw_include";i:1;s:9:"dw_logged";s:0:"";s:9:"other_ids";s:0:"";s:7:"page-22";i:1;}i:4;a:8:{s:5:"title";s:0:"";s:7:"content";s:380:"<div class="vidjet-wrap">\r\n<div class="vidjet book">\r\n	<a href="/book/" title="LAIKS IET - Book"><img src="/wp-content/uploads/2018/09/book.jpg" alt="LAIKS IET - Book"></a>\r\n    <a href="/book/" title="LAIKS IET - Book">\r\n    	<span>CLICK HERE TO GET MY STORY</span>\r\n        <span>IT’S TIME</span>\r\n        <span>THE TALES OF MY NOMAD SOUL</span>\r\n    </a>	\r\n</div>    \r\n</div>";s:10:"dw_include";i:1;s:9:"dw_logged";s:0:"";s:9:"other_ids";s:0:"";s:7:"page-22";i:1;s:7:"page-24";i:1;s:5:"cat-3";i:1;}i:5;a:6:{s:5:"title";s:0:"";s:7:"content";s:283:"<div class="cont grey ph30">\r\n<div class="wrapper">\r\n	<div class="twm-block">\r\n		<div class="text">I am another you - you are another me. So just say Hi and find out about my next adventure.</div>\r\n   	 	<a href="/contacts/" class="button">Press here</a>\r\n	</div>\r\n</div>    \r\n</div>";s:10:"dw_include";i:1;s:9:"dw_logged";s:0:"";s:9:"other_ids";s:0:"";s:10:"page-front";i:1;}i:6;a:6:{s:5:"title";s:0:"";s:7:"content";s:230:"<div class="cont grey ph30">\r\n<div class="wrapper">\r\n	<div class="twm-block">\r\n		<div class="text">Send me your feedback or question about it.</div>\r\n   	 	<a href="/contacts/" class="button">Нere</a>\r\n	</div>\r\n</div>    \r\n</div>";s:10:"dw_include";i:1;s:9:"dw_logged";s:0:"";s:9:"other_ids";s:0:"";s:7:"page-26";i:1;}s:12:"_multiwidget";i:1;}', 'yes'),
